// src/App.js
import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  // Define your phrases
  const phrases = [
    "Hello World",
    "I'm Jane Doe",
    "A Web Developer",
    "Creating Awesome Experiences",
    "Welcome to My Portfolio"
  ];

  // State to track the current phrase index
  const [currentPhraseIndex, setCurrentPhraseIndex] = useState(0);

  // Update the phrase index every 4 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentPhraseIndex((prevIndex) => (prevIndex + 1) % phrases.length);
    }, 5000); // 4 seconds

    return () => clearInterval(interval); // Cleanup on unmount
  }, [phrases.length]);

  return (
    <div className="App">
      {/* Background Container */}
      <div className="container">
        <div></div>
        <div></div>
        <div></div>
        <div></div>
        <div></div>
      </div>

      {/* Foreground Content */}
      <div className="content">
        {/* First Phrase */}
        <p
          className="hello"
          key={currentPhraseIndex}
        >
          {phrases[currentPhraseIndex]}
        </p>

        {/* Second Phrase */}
        <p
          className="hello2"
          key={currentPhraseIndex + 1}
        >
          {phrases[(currentPhraseIndex + 1) % phrases.length]}
        </p>
      </div>
    </div>
  );
}

export default App;
