import './App.css';
import React, { useState } from 'react';

function App() {
  const [backgroundColor, setBackgroundColor] = useState('blue');
  return (
    <div
    style={{
      backgroundColor,
      transition: 'background-color 1s ease',
    }}>
      <button onClick={() => setBackgroundColor('red')}>Change Color</button>
    </div>
  );
}

export default App;
